+        return isNested() ?
+                ((ClassSourceBuilder)enclosing).className() + "." + className() :
+                className();
+    }
+
+    @Override
+    public final String packageName() {
+        return desc.packageName();
+    }
+
+    String superClass() {
+        return null;
+    }
+
+    String mods() {
