+        append(mods());
+        append(kind.kindName + " " + className());
+        if (superClass() != null) {
+            append(" extends ");
+            append(superClass());
+        }
+        append(" {\n\n");
+    }
+
+    JavaSourceBuilder classEnd() {
+        indent();
+        append("}\n\n");
+        if (isNested()) {
+            decrAlign();
+            ((ClassSourceBuilder)enclosing).append(build());
+            sb = null;
+        }
+        return enclosing;
+    }
+
+    @Override
+    public List<JavaFileObject> toFiles() {
+        if (isNested()) {
+            throw new UnsupportedOperationException("Nested builder!");
+        }
