+
+            Entry failure(Type type) {
+                throw new IllegalStateException();
+            }
+
+            boolean isSuccess() {
+                return false;
+            }
+            boolean isRecoverableFailure() {
+                return false;
+            }
+            boolean isUnparsed() {
+                return false;
+            }
+
+            void update() {
+                macrosByMangledName.put(mangledName(), this);
+            }
+        }
+
+        class Unparsed extends Entry {
