+        }
+
+        @Override
+        public String visitArray(Type.Array t, Void unused) {
+            return firstUnsupportedType(t.elementType());
+        }
+
+        @Override
+        public String visitType(Type t, Void unused) {
+            return null;
+        }
+    };
+}

