+            { "global_short",  short.class,  C_SHORT, (short) 2  },
+            { "global_int",    int.class,    C_INT,           3  },
+            { "global_long",   long.class,   C_LONG_LONG,      4L },
+            { "global_float",  float.class,  C_FLOAT,         5F },
+            { "global_double", double.class, C_DOUBLE,        6D },
