+            { "Foo", C_SHORT.withName("s"),     short.class, (short) 10  },
+            { "Foo", C_INT.withName("i"),       int.class,           10  },
+            { "Foo", C_LONG_LONG.withName("ll"), long.class,          10L },
+            { "Foo", C_FLOAT.withName("f"),     float.class,         10F },
+            { "Foo", C_DOUBLE.withName("d"),    double.class,        10D },
