-  monitor_notify(jvmti, env, AT_LINE);
-
-  // Wait for agent to terminate
-  while (test_state != Terminated) {
-    monitor_wait(jvmti, env, AT_LINE);
-  }
-
-  monitor_exit(jvmti, env, AT_LINE);
