-    private static String extractStringOptionWithDelimiter(String opName, String s, String deflt, char delimiter) {
-        int p = s.indexOf(opName+"=");
-        if (p == -1) return deflt;
-        p+=opName.length()+1;
-        int pe = s.indexOf(delimiter, p);
-        if (pe == -1) pe = s.length();
-        return s.substring(p, pe);
