-        p+=opName.length()+1;
-        int pe = s.indexOf(',', p);
-        if (pe == -1) pe = s.length();
-        int v = 0;
-        try {
-            v = Integer.parseInt(s.substring(p, pe));
-        } catch (Exception e) {}
-        return v;
-    }
