+  ld_ptr(R0, JavaThread::cont_fastpath_offset(), R16_thread);
+  cmpld(CCR0, R1_SP, R0);
+  ble(CCR0, done);
+  st_ptr(R1_SP, JavaThread::cont_fastpath_offset(), R16_thread);
+  bind(done);
