+void MacroAssembler::pop_cont_fastpath() {
+  Label done;
+  ld_ptr(R0, JavaThread::cont_fastpath_offset(), R16_thread);
+  cmpld(CCR0, R1_SP, R0);
+  ble(CCR0, done);
+  li(R0, 0);
+  st_ptr(R0, JavaThread::cont_fastpath_offset(), R16_thread);
+  bind(done);
+}
