-        addr_index++;
-    }
-    (*env)->SetObjectField(env, netifObj, ni_addrsID, addrArr);
-    (*env)->DeleteLocalRef(env, addrArr);
-    (*env)->SetObjectField(env, netifObj, ni_bindsID, bindsArr);
