+    + hb_range (c->plan->num_output_glyphs ())
+    | hb_map (reverse_glyph_map)
+    | hb_filter (glyphset)
+    | hb_map_retains_sorting ([&](hb_codepoint_t old_gid)
+                              {
+                                const BaseGlyphRecord* old_record = get_base_glyph_record (old_gid);
+                                hb_vector_t<LayerRecord> out_layers;
