+                                if (unlikely (!old_record ||
+                                              old_record->firstLayerIdx >= numLayers ||
+                                              old_record->firstLayerIdx + old_record->numLayers > numLayers))
+                                  return hb_pair_t<bool, hb_vector_t<LayerRecord>> (false, out_layers);
