+  protected:
+  UnsizedArrayOf<Offset32To<SBIXGlyph>>
+                imageOffsetsZ;  /* Offset from the beginning of the strike data header
+                                 * to bitmap data for an individual glyph ID. */
+  public:
+  DEFINE_SIZE_ARRAY (4, imageOffsetsZ);
+};
