+  union {
+  FixedVersion<>                version;        /* Version identifier */
+  GDEFVersion1_2<SmallTypes>    version1;
+#ifndef HB_NO_BEYOND_64K
+  GDEFVersion1_2<MediumTypes>   version2;
+#endif
+  } u;
+  public:
+  DEFINE_SIZE_MIN (4);
+};
