+        long l = 111111111111111111L;
+        if (l * 81 == 1L) {
+            iFld = 23;
+        }
+        if (l * 81 == 8999999999999999991L) {
+            lFld = 23;
+        }
+        if (l * 83 == 1L) {
+            iFld2 = 34;
+        }
+        if (l * 83 == 9222222222222222213L) {
+            lFld2 = 23;
+        }
+    }
