+                    SelectionKey key = ssc.register(sel, SelectionKey.OP_ACCEPT);
+                    CountDownLatch opStartLatch = new CountDownLatch(1);
+                    runTest(executor, key, () -> {
+                        // notify that we will now start accept()ing
+                        opStartLatch.countDown();
