+          *ptr = node->_next;
+          if (ALLOC_TYPE == ResourceObj::C_HEAP) {
+            delete node;
+          }
+          _number_of_entries --;
+        } else {
+          ptr = &(node->_next);
+        }
+      }
+    }
+  }
