+                    }
+                    systemLoad += cpuLoad;
+                }
+                return systemLoad / cpuSet.length;
+            };
+        }
+
+        @Override
+        protected LongSupplier cpuUsageSupplier() {
+            return () -> containerMetrics.getCpuUsage();
+        }
+
+    }
