+      case BoolTest::ge: sve_fcmge(pd, size, pg, zn, zm); break;
+      case BoolTest::gt: sve_fcmgt(pd, size, pg, zn, zm); break;
+      case BoolTest::le: sve_fcmge(pd, size, pg, zm, zn); break;
