+        private static void toByteArray(long v0, long v1, long v2, long v3,
+            long v4, byte[] dst) {
+
+            dst[0] = (byte) v0;
+            v0 >>= 8;
+            dst[1] = (byte) v0;
+            v0 >>= 8;
+            dst[2] = (byte) v0;
+            v0 >>= 8;
+            dst[3] = (byte) v0;
+
+            dst[3] += (v1 & 0x3F) << 2;
+            v1 >>= 6;
+            dst[4] = (byte) v1;
+            v1 >>= 8;
+            dst[5] = (byte) v1;
+            v1 >>= 8;
+            dst[6] = (byte) v1;
+
+            dst[6] += (v2 & 0xF) << 4;
+            v2 >>= 4;
+            dst[7] = (byte) v2;
+            v2 >>= 8;
+            dst[8] = (byte) v2;
+            v2 >>= 8;
+            dst[9] = (byte) v2;
+
+            dst[9] += (v3 & 0x3) << 6;
+            v3 >>= 2;
+            dst[10] = (byte) v3;
+            v3 >>= 8;
+            dst[11] = (byte) v3;
+            v3 >>= 8;
+            dst[12] = (byte) v3;
+
+            dst[13] = (byte) v4;
+            v4 >>= 8;
+            dst[14] = (byte) v4;
+            v4 >>= 8;
+            dst[15] = (byte) v4;
+        }
