+    }
+
+    byte[] fillByte(int size, IntFunction<Byte> f) {
+        byte[] array = new byte[size];
+        for (int i = 0; i < size; i++) {
+            array[i] = f.apply(i);
+        }
+        return array;
+    }
+
+    int[] fillInt(int size, IntFunction<Integer> f) {
+        int[] array = new int[size];
+        for (int i = 0; i < array.length; i++) {
+            array[i] = f.apply(i);
