+package org.openjdk.bench.jdk.incubator.vector.operation;
+
+import jdk.incubator.vector.*;
+import jdk.incubator.vector.VectorSpecies;
+import org.openjdk.jmh.annotations.*;
+
+import java.util.Arrays;
+import java.util.concurrent.TimeUnit;
+
+@BenchmarkMode(Mode.Throughput)
+@Warmup(iterations = 3, time = 1)
+@Measurement(iterations = 5, time = 1)
+@OutputTimeUnit(TimeUnit.MILLISECONDS)
+@State(Scope.Benchmark)
+@Fork(value = 1, jvmArgsPrepend = {"--add-modules=jdk.incubator.vector"})
+public class Merge extends AbstractVectorBenchmark {
+
+    @Param({"64", "1024", "65536"})
+    int size;
+
+    int[] in, out;
+
+    @Setup
+    public void setup() {
+        size = size + (size % 64); // FIXME: process tails
+        in  = new int[size];
+        out = new int[size];
+        for (int i = 0; i < size; i++) {
+            in[i] = i;
+        }
+    }
+
+    @Benchmark
+    public void merge64_128() {
+        merge(I64, I128);
+    }
+
+    @Benchmark
+    public void merge128_256() {
+        merge(I128, I256);
+    }
+
+    @Benchmark
+    public void merge256_512() {
+        merge(I256, I512);
+    }
+
+    @Benchmark
+    public void merge64_256() {
+        merge(I64, I256);
+    }
+
+    @Benchmark
+    public void merge128_512() {
+        merge(I128, I512);
+    }
+
+    @Benchmark
+    public void merge64_512() {
+        merge(I64, I256);
+    }
+
+    IntVector merge(VectorSpecies<Integer> from, VectorSpecies<Integer> to, int idx) {
+        assert from.length() <= to.length();
+
+        int vlenFrom = from.length();
+        int vlenTo   =   to.length();
+
+        if (vlenFrom == vlenTo) {
+            return IntVector.fromArray(from, in, idx);
+        } else {
+            var stepDown = narrow(to);
+            int mid = stepDown.length();
+            var lo = merge(from, stepDown, idx);
+            var hi = merge(from, stepDown, idx + mid);
+            return join(stepDown, to, lo, hi);
+        }
+    }
+
+
+    void merge(VectorSpecies<Integer> from, VectorSpecies<Integer> to) {
+        int vlenTo = to.length();
+        for (int i = 0; i < in.length; i += vlenTo) {
+            var r = merge(from, to, i);
+            r.intoArray(out, i);
+        }
+    }
+
+    @TearDown
+    public void tearDown() {
+        assert Arrays.deepEquals(new Object[] {in}, new Object[] {out});
+    }
+}

