+        char[] da = dst.array();
+        int dp = dst.arrayOffset() + dst.position();
+        int dl = dst.arrayOffset() + dst.limit();
+        int dlASCII = dp + Math.min(sl - sp, dl - dp);
+
+        // ASCII only loop
+        while (dp < dlASCII && sa[sp] >= 0)
+            da[dp++] = (char) sa[sp++];
+        while (sp < sl) {
+            int b1 = sa[sp];
+            if (b1 >= 0) {
+                // 1 byte, 7 bits: 0xxxxxxx
+                if (dp >= dl)
+                    return xflow(src, sp, sl, dst, dp, 1);
+                da[dp++] = (char) b1;
+                sp++;
+            } else if ((b1 >> 5) == -2 && (b1 & 0x1e) != 0) {
+                // 2 bytes, 11 bits: 110xxxxx 10xxxxxx
+                //                   [C2..DF] [80..BF]
+                if (sl - sp < 2 || dp >= dl)
+                    return xflow(src, sp, sl, dst, dp, 2);
+                int b2 = sa[sp + 1];
+                // Now we check the first byte of 2-byte sequence as
+                //     if ((b1 >> 5) == -2 && (b1 & 0x1e) != 0)
+                // no longer need to check b1 against c1 & c0 for
+                // malformed as we did in previous version
+                //   (b1 & 0x1e) == 0x0 || (b2 & 0xc0) != 0x80;
+                // only need to check the second byte b2.
+                if (isNotContinuation(b2))
+                    return malformedForLength(src, sp, dst, dp, 1);
+                da[dp++] = (char) (((b1 << 6) ^ b2)
+                                   ^
+                                   (((byte) 0xC0 << 6) ^
+                                    ((byte) 0x80 << 0)));
+                sp += 2;
+            } else if ((b1 >> 4) == -2) {
+                // 3 bytes, 16 bits: 1110xxxx 10xxxxxx 10xxxxxx
+                int srcRemaining = sl - sp;
+                if (srcRemaining < 3 || dp >= dl) {
+                    if (srcRemaining > 1 && isMalformed3_2(b1, sa[sp + 1]))
+                        return malformedForLength(src, sp, dst, dp, 1);
+                    return xflow(src, sp, sl, dst, dp, 3);
+                }
+                int b2 = sa[sp + 1];
+                int b3 = sa[sp + 2];
+                if (isMalformed3(b1, b2, b3))
+                    return malformed(src, sp, dst, dp, 3);
+                char c = (char)
+                    ((b1 << 12) ^
+                     (b2 <<  6) ^
+                     (b3 ^
+                      (((byte) 0xE0 << 12) ^
+                       ((byte) 0x80 <<  6) ^
+                       ((byte) 0x80 <<  0))));
+                if (Character.isSurrogate(c))
+                    return malformedForLength(src, sp, dst, dp, 3);
+                da[dp++] = c;
+                sp += 3;
+            } else if ((b1 >> 3) == -2) {
+                // 4 bytes, 21 bits: 11110xxx 10xxxxxx 10xxxxxx 10xxxxxx
+                int srcRemaining = sl - sp;
+                if (srcRemaining < 4 || dl - dp < 2) {
+                    b1 &= 0xff;
+                    if (b1 > 0xf4 ||
+                        srcRemaining > 1 && isMalformed4_2(b1, sa[sp + 1] & 0xff))
+                        return malformedForLength(src, sp, dst, dp, 1);
+                    if (srcRemaining > 2 && isMalformed4_3(sa[sp + 2]))
+                        return malformedForLength(src, sp, dst, dp, 2);
+                    return xflow(src, sp, sl, dst, dp, 4);
+                }
+                int b2 = sa[sp + 1];
+                int b3 = sa[sp + 2];
+                int b4 = sa[sp + 3];
+                int uc = ((b1 << 18) ^
+                          (b2 << 12) ^
+                          (b3 <<  6) ^
+                          (b4 ^
+                           (((byte) 0xF0 << 18) ^
+                            ((byte) 0x80 << 12) ^
+                            ((byte) 0x80 <<  6) ^
+                            ((byte) 0x80 <<  0))));
+                if (isMalformed4(b2, b3, b4) ||
+                    // shortest form check
+                    !Character.isSupplementaryCodePoint(uc)) {
+                    return malformed(src, sp, dst, dp, 4);
+                }
+                da[dp++] = Character.highSurrogate(uc);
+                da[dp++] = Character.lowSurrogate(uc);
+                sp += 4;
+            } else
+                return malformed(src, sp, dst, dp, 1);
+        }
+        return xflow(src, sp, sl, dst, dp, 0);
+    }
+
+    private static CoderResult xflow(Buffer src, int sp, int sl,
+                                     Buffer dst, int dp, int nb) {
+        updatePositions(src, sp, dst, dp);
+        return (nb == 0 || sl - sp < nb)
+               ? CoderResult.UNDERFLOW : CoderResult.OVERFLOW;
+    }
+
+    private static CoderResult malformedForLength(ByteBuffer src,
+                                                  int sp,
+                                                  CharBuffer dst,
+                                                  int dp,
+                                                  int malformedNB)
+    {
+        updatePositions(src, sp, dst, dp);
+        return CoderResult.malformedForLength(malformedNB);
+    }
+
+    private static CoderResult malformed(ByteBuffer src, int sp,
+                                         CharBuffer dst, int dp,
+                                         int nb)
+    {
+        src.position(sp - src.arrayOffset());
+        CoderResult cr = malformedN(src, sp, nb);
+        updatePositions(src, sp, dst, dp);
+        return cr;
+    }
+
+    private static CoderResult malformedN(ByteBuffer src, int sp,
+                                          int nb) {
+        switch (nb) {
+        case 1:
+        case 2:                    // always 1
+            return CoderResult.malformedForLength(1);
+        case 3:
+            int b1 = src.get();
+            int b2 = src.get();    // no need to lookup b3
+            return CoderResult.malformedForLength(
+                ((b1 == (byte)0xe0 && (b2 & 0xe0) == 0x80) ||
+                 isNotContinuation(b2)) ? 1 : 2);
+        case 4:  // we don't care the speed here
+            b1 = src.get() & 0xff;
+            b2 = src.get() & 0xff;
+            if (b1 > 0xf4 ||
+                (b1 == 0xf0 && (b2 < 0x90 || b2 > 0xbf)) ||
+                (b1 == 0xf4 && (b2 & 0xf0) != 0x80) ||
+                isNotContinuation(b2))
+                return CoderResult.malformedForLength(1);
+            if (isNotContinuation(src.get()))
+                return CoderResult.malformedForLength(2);
+            return CoderResult.malformedForLength(3);
+        default:
+            assert false;
+            return null;
+        }
+    }
+
+    private static boolean isNotContinuation(int b) {
+        return (b & 0xc0) != 0x80;
+    }
+
+    //  [E0]     [A0..BF] [80..BF]
+    //  [E1..EF] [80..BF] [80..BF]
+    private static boolean isMalformed3(int b1, int b2, int b3) {
+        return (b1 == (byte)0xe0 && (b2 & 0xe0) == 0x80) ||
+               (b2 & 0xc0) != 0x80 || (b3 & 0xc0) != 0x80;
+    }
+
+    // only used when there is only one byte left in src buffer
+    private static boolean isMalformed3_2(int b1, int b2) {
+        return (b1 == (byte)0xe0 && (b2 & 0xe0) == 0x80) ||
+               (b2 & 0xc0) != 0x80;
+    }
+
+    //  [F0]     [90..BF] [80..BF] [80..BF]
+    //  [F1..F3] [80..BF] [80..BF] [80..BF]
+    //  [F4]     [80..8F] [80..BF] [80..BF]
+    //  only check 80-be range here, the [0xf0,0x80...] and [0xf4,0x90-...]
+    //  will be checked by Character.isSupplementaryCodePoint(uc)
+    private static boolean isMalformed4(int b2, int b3, int b4) {
+        return (b2 & 0xc0) != 0x80 || (b3 & 0xc0) != 0x80 ||
+               (b4 & 0xc0) != 0x80;
+    }
+
+    // only used when there is less than 4 bytes left in src buffer.
+    // both b1 and b2 should be "& 0xff" before passed in.
+    private static boolean isMalformed4_2(int b1, int b2) {
+        return (b1 == 0xf0 && (b2  < 0x90 || b2 > 0xbf)) ||
+               (b1 == 0xf4 && (b2 & 0xf0) != 0x80) ||
+               (b2 & 0xc0) != 0x80;
+    }
+
+    // tests if b1 and b2 are malformed as the first 2 bytes of a
+    // legal`4-byte utf-8 byte sequence.
+    // only used when there is less than 4 bytes left in src buffer,
+    // after isMalformed4_2 has been invoked.
+    private static boolean isMalformed4_3(int b3) {
+        return (b3 & 0xc0) != 0x80;
+    }
+
+    private static void updatePositions(Buffer src, int sp,
+                                        Buffer dst, int dp) {
+        src.position(sp - src.arrayOffset());
+        dst.position(dp - dst.arrayOffset());
+    }
+}
