+  CDSHeapVerifier();
+  ~CDSHeapVerifier();
+
+public:
+
+  // Overrides KlassClosure::do_klass()
+  virtual void do_klass(Klass* k);
+
+  // For ResourceHashtable::iterate()
+  inline bool do_entry(oop& orig_obj, HeapShared::CachedOopInfo& value);
+
+  static void verify() NOT_DEBUG_RETURN;
+};
