+            public boolean shouldYieldFocus(JComponent input) {
+                ++shouldYieldFocusCount;
+                System.out.println("InputVerifier.shouldYieldFocus() called " + shouldYieldFocusCount);
+                return false;
+            }
+        };
+
+        jTextField2.setInputVerifier(iv);
+
