+/*
+ * @test
+ * @key headful
+ * @bug 8275715
+ * @summary Tests that paint method is not called twice
+ * @run main/othervm MultiPaintEventTest
+ */
+
+import java.awt.*;
+
+public class MultiPaintEventTest extends Canvas {
+
+    private int count = 0;
+    private final Object lock = new Object();
+
+    public void paint(Graphics g) {
+        synchronized(lock) {
+            count++;
+        }
+
+        int w = getWidth();
+        int h = getHeight();
+
+        Graphics2D g2d = (Graphics2D)g;
+        if (count % 2 == 1) {
+            g2d.setColor(Color.green);
+        } else {
+            g2d.setColor(Color.red);
+        }
+        g2d.fillRect(0, 0, w, h);
+    }
+
+    public int getCount() {
+        synchronized(lock) {
+            return count;
+        }
+    }
+
+    public Dimension getPreferredSize() {
+        return new Dimension(400, 400);
+    }
+
+    public static void main(String[] args) {
+        MultiPaintEventTest test = new MultiPaintEventTest();
+        Frame frame = new Frame();
+        frame.setUndecorated(true);
+        frame.add(test);
+        frame.pack();
+        frame.setLocationRelativeTo(null);
+        frame.setVisible(true);
+
+        try {
+            Thread.sleep(2000);
+            if (test.getCount() > 1) {
+                throw new RuntimeException("Processed unnecessary paint().");
+            }
+        } catch (InterruptedException ex) {
+            throw new RuntimeException("Failed: Interrupted");
+        } finally {
+            frame.dispose();
+        }
+    }
+}

