+    vector_popcount_byte(xtmp1, src, dst, xtmp2, rtmp, vec_enc);
+    vpxorq(xtmp2, xtmp2, xtmp2, vec_enc);
+    vpsadbw(dst, xtmp1, xtmp2, vec_enc);
