+  evaddsh(vtmp1, vtmp1, vtmp);
+  pshuflw(vtmp, src, 0x02);
+  evaddsh(vtmp1, vtmp1, vtmp);
+  pshuflw(vtmp, src, 0x03);
+  evaddsh(vtmp1, vtmp1, vtmp);
+  pshufd(vtmp2, src, 0x0E);
+  evaddsh(vtmp1, vtmp1, vtmp2);
+  pshuflw(vtmp, vtmp2, 0x01);
+  evaddsh(vtmp1, vtmp1, vtmp);
+  pshuflw(vtmp, vtmp2, 0x02);
+  evaddsh(vtmp1, vtmp1, vtmp);
+  pshuflw(vtmp, vtmp2, 0x03);
+  evaddsh(vtmp1, vtmp1, vtmp);
+  movdl(dst, vtmp1);
+}
