+    static short cornerCaseValue(int i) {
+        switch(i % 7) {
+            case 0:
+                return Halffloat.MAX_VALUE;
+            case 1:
+                return Halffloat.MIN_VALUE;
+            case 2:
+                return Halffloat.NEGATIVE_INFINITY;
+            case 3:
+                return Halffloat.POSITIVE_INFINITY;
+            case 4:
+                return Halffloat.NaN;
+            case 5:
+                return (short)0.0;
+            default:
+                return Short.MIN_VALUE;
+        }
+    }
