+    private static JFrame createTestUI() {
+        JFrame frame = new JFrame("bug4187996");
+        JMenu submenu = new JMenu("Submenu");
+        submenu.add(new JMenuItem("Sub 1"));
+        submenu.add(new JMenuItem("Sub 2"));
+
+        JMenu menu = new JMenu("Menu");
