+public:
+  ShenandoahOldGeneration(uint max_queues, size_t max_capacity, size_t soft_max_capacity);
+
+  virtual ShenandoahHeuristics* initialize_heuristics(ShenandoahMode* gc_mode) override;
+
+  const char* name() const override {
+    return "OLD";
+  }
