+    /**
+     * Constructor.
+     */
+    private DecimalDigits() {
+    }
+
+    @Override
+    public int digits(long value, byte[] buffer, int index,
+                      MethodHandle putCharMH) throws Throwable {
+        boolean negative = value < 0;
+        if (!negative) {
+            value = -value;
+        }
+
+        long q;
+        int r;
+        while (value <= Integer.MIN_VALUE) {
+            q = value / 100;
+            r = (int)((q * 100) - value);
+            value = q;
+            int digits = DIGITS[r];
