+        while (ivalue <= -100) {
+            iq = ivalue / 100;
+            r = (iq * 100) - ivalue;
+            ivalue = iq;
+            int digits = DIGITS[r];
+            putCharMH.invokeExact(buffer, --index, digits >> 8);
+            putCharMH.invokeExact(buffer, --index, digits & 0xFF);
+        }
+
+        if (ivalue < 0) {
+            ivalue = -ivalue;
+        }
+
+        int digits = DIGITS[ivalue];
+        putCharMH.invokeExact(buffer, --index, digits >> 8);
+
+        if (9 < ivalue) {
+            putCharMH.invokeExact(buffer, --index, digits & 0xFF);
+        }
+
+        if (negative) {
+            putCharMH.invokeExact(buffer, --index, (int)'-');
+        }
