+    private String parse(String desc) {
+        int end = desc.indexOf(")");
+        if (end == -1)
+            throw new AssertionError();
+        end ++;
+        int p = 0;
+        StringBuilder sb = new StringBuilder();
+        int dims = 0;
+
+        while (p < end) {
+            String type;
+            switch (desc.charAt(p++)) {
+                case '(' -> {
+                    sb.append('(');
+                    continue;
+                }
+                case ')' -> {
+                    sb.append(')');
+                    continue;
+                }
+                case '[' -> {
+                    dims++;
+                    continue;
+                }
+                case 'B' -> type = "byte";
+                case 'C' -> type = "char";
+                case 'D' -> type = "double";
+                case 'F' -> type = "float";
+                case 'I' -> type = "int";
+                case 'J' -> type = "long";
+                case 'L' -> {
+                    int sep = desc.indexOf(';', p);
+                    if (sep == -1)
+                        throw new AssertionError();
+                    type = desc.substring(p, sep).replace('/', '.');
+                    p = sep + 1;
+                }
+                case 'S' -> type = "short";
+                case 'Z' -> type = "boolean";
+                case 'V' -> type = "void";
+                default -> throw new AssertionError();
+            }
+
+            if (sb.length() > 1 && sb.charAt(0) == '(')
+                sb.append(", ");
+            sb.append(type);
+            for ( ; dims > 0; dims-- )
+                sb.append("[]");
+        }
+        return sb.toString();
+    }
+

