-    0x00000000UL, 0xbfd00000UL, // C1_0 = -0.25
-    0x92492492UL, 0x3fc24924UL, // C1_1 = 0.14285714285714285
-    0x55555555UL, 0x3fd55555UL, // C2_0 = 0.3333333333333333
