+package org.openjdk.bench.vm.compiler;
+
+import org.openjdk.jmh.annotations.Benchmark;
+import org.openjdk.jmh.annotations.*;
+
+import java.util.concurrent.TimeUnit;
+import org.openjdk.jmh.infra.Blackhole;
+
+/* test allocation speed of object with final field */
+@BenchmarkMode(Mode.Throughput)
+@OutputTimeUnit(TimeUnit.SECONDS)
+@State(Scope.Benchmark)
+@Warmup(iterations = 5, time = 3, timeUnit = TimeUnit.SECONDS)
+@Measurement(iterations = 3, time = 3, timeUnit = TimeUnit.SECONDS)
+@Fork(value = 3)
+public class FinalFieldInitialize {
+  final static int LEN = 100_000;
+  Object arr[] = null;
+  @Setup
+  public void setup(){
+    arr = new Object[LEN];
+  }
+
+  @Benchmark
+  public void testAlloc(Blackhole bh) {
+    for (int i=0; i<LEN; i++) {
+      arr[i] = new TObj();
+    }
+    bh.consume(arr);
+  }
+
+  @Benchmark
+  public void testAllocWithFinal(Blackhole bh) {
+    for (int i=0; i<LEN; i++) {
+      arr[i] = new TObjWithFinal();
+    }
+    bh.consume(arr);
+  }
+}
+
+class TObj {
+  private int i;
+  private long l;
+  private boolean b;
+
+  public TObj() {
+    i = 10;
+    l = 100L;
+    b = true;
+  }
+}
+
+class TObjWithFinal {
+  private int i;
+  private long l;
+  private final boolean b;
+
+  public TObjWithFinal() {
+    i = 10;
+    l = 100L;
+    b = true;
+  }
+}
