+                    scores[SCORE_OTHER], (USE_FPS ? "ms" : "FPS"),
+                    results[PCT_00], results[PCT_10], results[PCT_25], results[PCT_50], results[PCT_75], results[PCT_90], results[PCT_100],
+                    (USE_FPS ? "FPS" : "ms"),
+                    frames);
+            }
