+            return isValid(System.currentTimeMillis());
+        }
+
+        public void invalidate() {
+            clear();
+            key = null;
+            expirationTime = -1;
+        }
+
+        public void clear() {
+            queue.forEach(CacheEntry::invalidate);
+            queue.clear();
+        }
+
+        public boolean isEmpty() {
+            return queue.isEmpty();
+        }
