+    private static boolean isCompilerFlag(String string) {
+        boolean flag = false;
+        for (String include : COMPILER_FLAGS_INCLUDE) {
+            if (string.startsWith(include)) {
+                flag = true;
+                break;
+            }
+        }
+        for (String is : COMPILER_FLAGS_IS) {
+            if (string.equals(is)) {
+                flag = true;
+                break;
+            }
+        }
+        if (isExcludeCompilerFlag(string)) {
+            flag = false;
+        }
+        return flag;
+    }
