+    private static String extractPath(String string, String from, String to) {
+        String result = null;
+        String[] tokens = string.split("/");
+        int i = 0;
+        for (; i < tokens.length; i++) {
+            if (tokens[i].equals(from)) {
+                result = "";
+                break;
+            }
+        }
+        for (; i < tokens.length; i++) {
+            result += "/" + tokens[i];
+            if (tokens[i].equals(to)) {
+                break;
+            }
+        }
+        return result;
+    }
