+            System.setProperty("test.java.opts",
+                "-Dtest.src=" + System.getProperty("test.src") +
+                    " -Dtest.jdk=" + System.getProperty("test.jdk") +
+                    " -Dtest.root=" + System.getProperty("test.root") +
+                    " -Djavax.net.debug=ssl,handshake " +
