+    public int insertNewLayerIfNeeded(LayoutNode node, int layerNr) {
+        for (Link inputLink : getInputLinks(node.getVertex())) {
+            if (inputLink.getFrom().getVertex() == inputLink.getTo().getVertex()) continue;
