+    private void assertDoesNotThrow(ThrowingRunnable runnable, String message) {
+        try {
+            runnable.run();
+        } catch (Throwable t) {
+            Assert.fail(message + "\n Exception thrown: " + t.getMessage());
+        }
+    }
