+    private void postReply(Path file, Email mail) {
+        var mboxMail = Mbox.fromMail(mail);
+        try {
+            Files.writeString(file, mboxMail, StandardCharsets.UTF_8, StandardOpenOption.APPEND);
+        } catch (IOException e) {
+            throw new UncheckedIOException(e);
+        }
+    }
+
