+        List<PullRequest> prs = request.get("pulls")
+                .param("state", "all")
+                .param("sort", "updated")
+                .param("direction", "desc")
+                .param("per_page", "100")
+                .maxPages(2)
+                .execute().asArray().stream()
+                .map(jsonValue -> new GitHubPullRequest(this, jsonValue, request))
+                .filter(pr -> !pr.updatedAt().isBefore(updatedAfter))
+                .collect(Collectors.toList());
+        // Check if any PRs were returned out of order and log it to keep track
+        // of if GitHub ever stops this insanity.
+        PullRequest previousPr = null;
+        for (PullRequest pr : prs) {
+            if (previousPr != null && previousPr.updatedAt().isBefore(pr.updatedAt())) {
+                log.info("GitHub PR for " + name() + " listed out of order: " + pr.id() + " updatedAt: " + pr.updatedAt()
+                        + " listed after " + previousPr.id() + " updatedAt: " + previousPr.updatedAt());
+            } else {
+                previousPr = pr;
+            }
+        }
+        return prs;
