+                // so overflows.
+                {0x1.3p1f, 0x1.afp15f, -MAX_VAL_FP16,
+                 InfinityF},
+
+                // Product exactly equals 0x1.ffep15, the overflow
+                // threshold; subtracting a non-zero finite value will
+                // result in MAX_VALUE, adding zero or a positive
+                // value will overflow.
