+        for (int i = 0; i < len; i++) {
+            char ch = name.charAt(i);
+            if (ch == '.' || ch == ';' || ch == '[' || ch == '/')
+                throw new IllegalArgumentException("Invalid member name: " + name);
+            if (method && (ch == '<' || ch == '>')) {
+                if (!pointyNames.contains(name))
