+    protected void reduce(long[] limbs) {
+        long b0 = limbs[0];
+        long b1 = limbs[1];
+        long b2 = limbs[2];
+        long b3 = limbs[3];
+        long b4 = limbs[4];
+        long carry = b4 >> 48; // max 16-bits
+        b4 -= carry << 48;
+
+        // 2^0 position
+        b0 += carry;
+        // -2^96
+        b1 -= carry << 44;
+        // -2^192
+        b3 -= carry << 36;
+        // 2^224
+        b4 += carry << 16;
+
+        b1 += b0 >> BITS_PER_LIMB;
+        b2 += b1 >> BITS_PER_LIMB;
+        b3 += b2 >> BITS_PER_LIMB;
+        b4 += b3 >> BITS_PER_LIMB;
+
+        b0 &= LIMB_MASK;
+        b1 &= LIMB_MASK;
+        b2 &= LIMB_MASK;
+        b3 &= LIMB_MASK;
+
+        long c0, c1, c2, c3, c4;
+        c0 = modulus[0] + b0;
+        c1 = modulus[1] + b1 + (c0 >> BITS_PER_LIMB);
+        c0 &= LIMB_MASK;
+        c2 = modulus[2] + b2 + (c1 >> BITS_PER_LIMB);
+        c1 &= LIMB_MASK;
+        c3 = modulus[3] + b3 + (c2 >> BITS_PER_LIMB);
+        c2 &= LIMB_MASK;
+        c4 = modulus[4] + b4 + (c3 >> BITS_PER_LIMB);
+        c3 &= LIMB_MASK;
+
